import {
    ClerkProvider,
    SignedIn,
    SignedOut,
    RedirectToSignIn,
    SignIn,
    SignUp,
    UserButton,
    useUser,
    SignOutButton,
} from "@clerk/clerk-react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import "./App.css";
import Home from "./Home.js";
import Login from "./Login.js";
import Inbox from "./Inbox.js";
import Settings from "./Settings.js";
import SignUpModal from "./SignUpModal";
import NavBar from "./NavBar";
import ParentHome from "./ParentHome.js";

if (!process.env.REACT_APP_CLERK_PUBLISHABLE_KEY) {
    throw new Error("Missing Publishable Key!");
}
const clerkPublishableKey = process.env.REACT_APP_CLERK_PUBLISHABLE_KEY;

function ProtectedSignedIn() {
    const { user } = useUser();
    return (
        <div style={{ backgroundColor: "white" }}>
            <h1 style={{ color: "red" }}>This page is protected!</h1>
            <p>Welcome, {user.firstName}</p>
            <UserButton />
        </div>
    );
}

function ProtectedRoute({ element }) {
    return (
        <div>
            <SignedIn>{element}</SignedIn>
            <SignedOut>
                <RedirectToSignIn />
            </SignedOut>
        </div>
    );
}

const myRoutes = createBrowserRouter([
    {
        path: "/",
        element: <ProtectedRoute element={<Home />} />,
    },
    {
        path: "/login",
        element: <SignIn routing="path" path="/sign-in" />,
    },
    {
        path: "/signup",
        element: <SignUp routing="path" path="/sign-up" />,
    },
    {
        path: "/Inbox",
        element: <ProtectedRoute element={<Inbox />} />,
    },
    {
        path: "/Settings",
        element: <ProtectedRoute element={<Settings />} />,
    },
    {
        path: "/Parents",
        element: <ProtectedRoute element={<ParentHome />} />,
    },
]);

function App() {
    return (
        <ClerkProvider
            publishableKey={clerkPublishableKey}
            navigate={(to) => myRoutes.navigate(to)}
        >
            <div>
                <NavBar />
                <RouterProvider router={myRoutes} />
            </div>
        </ClerkProvider>
    );
}

export default App;
