import React, { useState } from "react";

const SignUpModal = () => {
    const [userType, setUserType] = useState(""); // Track user type
    const [classKey, setClassKey] = useState(""); // Track class key

    const handleUserTypeChange = (type) => {
        setUserType(type);
    };

    const handleClassKeyChange = (event) => {
        setClassKey(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Logic to handle sign up based on userType and classKey
        console.log(`User Type: ${userType}, Class Key: ${classKey}`);
        onClose();
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Sign Up</h2>
                <div>
                    <button onClick={() => handleUserTypeChange("teacher")}>
                        I'm a Teacher
                    </button>
                    <button onClick={() => handleUserTypeChange("parent")}>
                        I'm a Parent
                    </button>
                </div>
                {userType && (
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="classKey" className="form-label">
                                Class Key Number
                            </label>
                            <input
                                type="text"
                                className="form-control"
                                id="classKey"
                                value={classKey}
                                onChange={handleClassKeyChange}
                            />
                        </div>
                        <button type="submit" className="btn btn-primary">
                            Sign Up
                        </button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default SignUpModal;
