import NavBar from "./NavBar";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const Home = () => {
    const navigate = useNavigate();

    const [posts, setPosts] = useState([]);
    const [newPost, setNewPost] = useState("");
    const [name, setName] = useState("");

    const handleSubmit = (event) => {
        event.preventDefault();
        const form = event.target;

        const post = {
            title: newPost,
            name: name || "Anonymous", // Use the name state or default to "Anonymous"
            time: new Date().toLocaleString(), // Get the current time
            likes: 0, // Initialize likes to 0
            comments: [],
        };

        setPosts([post, ...posts]);
        setNewPost(""); // clears the text area after submitting
        setName("");
    };

    const createPost = (event) => {
        setNewPost(event.target.value); // Update the newPost state
    };

    const handleNameChange = (event) => {
        setName(event.target.value); // Update the name state
    };

    const handleLike = (index) => {
        const updatedPosts = [...posts];
        updatedPosts[index].likes += 1;
        setPosts(updatedPosts);
    };

    const handleComment = (index, comment) => {
        const updatedPosts = [...posts];
        updatedPosts[index].comments.push(comment);
        setPosts(updatedPosts);
    };

    return (
        <div className="mainPage">
            <div className="row">
                <div className="col-3"></div>
                <div
                    className="col-6"
                    style={{
                        background: "white",
                        padding: 10,
                        border: "1px solid gray",
                        marginBottom: 20,
                        borderRadius: 10,
                    }}
                >
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="nameInput" className="form-label">
                            Name:
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="nameInput"
                            placeholder="Your name"
                            value={name}
                            onChange={handleNameChange}
                        />
                        <label
                            htmlFor="titleInput"
                            className="form-label"
                        ></label>
                        <textarea
                            className="form-control"
                            id="titleInput"
                            placeholder="Post a new update for parents."
                            style={{ width: "100%" }}
                            onChange={(e) => setNewPost(e.target.value)}
                        ></textarea>
                        <div className="text-center">
                            <button className="btn btn-secondary">📸</button>
                            <button className="btn btn-secondary">🎥</button>
                            <button
                                type="submit"
                                className="btn btn-primary post-btn"
                            >
                                Post
                            </button>
                        </div>
                    </form>
                </div>
            </div>
            <div className="posts-list">
                {posts.map((post, index) => (
                    <div key={index} className="post-box">
                        <p>
                            <strong>{post.name}</strong> - {post.time}
                        </p>
                        <p>{post.title}</p>
                        <div className="post-actions">
                            <button onClick={() => handleLike(index)}>
                                Like ({post.likes})
                            </button>
                            <button
                                onClick={() =>
                                    handleComment(index, "New comment!")
                                }
                            >
                                Comment
                            </button>
                        </div>
                        {post.comments.length > 0 && (
                            <div className="post-comments">
                                {post.comments.map((comment, i) => (
                                    <p key={i}>{comment}</p>
                                ))}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
