import React, { useEffect, useState } from "react";

const SignUpModal = ({ onClose }) => {
    const [userType, setUserType] = useState(""); // Track user type
    const [classKey, setClassKey] = useState(""); // Track class key
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    useEffect(() => {
        document.getElementById("modal-backdrop").classList.add("show");
    }, []);

    const handleClose = () => {
        document.getElementById("modal-backdrop").classList.remove("show");
        onClose();
    };

    const handleUserTypeChange = (type) => {
        setUserType(type);
    };

    const handleClassKeyChange = (event) => {
        setClassKey(event.target.value);
    };

    const handleSignUp = (event) => {
        event.preventDefault();
        console.log("User Info:", { firstName, lastName, email, password });
        handleClose();
    };

    return (
        <div id="modal-backdrop" className="modal-backdrop">
            <div className="modal-content">
                <button onClick={handleClose} className="close-button">
                    &times;
                </button>
                <h2>Sign Up</h2>
                <form onSubmit={handleSignUp}>
                    <div className="mb-3">
                        <label htmlFor="firstName" className="form-label">
                            First Name
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="firstName"
                            value={firstName}
                            onChange={(event) =>
                                setFirstName(event.target.value)
                            }
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="lastName" className="form-label">
                            Last Name
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="lastName"
                            value={lastName}
                            onChange={(event) =>
                                setLastName(event.target.value)
                            }
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="email" className="form-label">
                            Email Address
                        </label>
                        <input
                            type="email"
                            className="form-control"
                            id="email"
                            value={email}
                            onChange={(event) => setEmail(event.target.value)}
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">
                            Password
                        </label>
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            value={password}
                            onChange={(event) =>
                                setPassword(event.target.value)
                            }
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="classKey" className="form-label">
                            Class Key
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="classKey"
                            value={classKey}
                            onChange={(e) => setClassKey(e.target.value)}
                        />
                    </div>
                    <div>
                        <button onClick={() => handleUserTypeChange("teacher")}>
                            I'm a Teacher
                        </button>
                        <button onClick={() => handleUserTypeChange("parent")}>
                            I'm a Parent
                        </button>
                    </div>
                    <button type="submit" className="btn btn-primary">
                        Sign Up
                    </button>
                </form>
            </div>
        </div>
    );
};

export default SignUpModal;
