import NavBar from "./NavBar";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const Home = () => {
    const navigate = useNavigate();

    const [posts, setPosts] = useState([]);
    const [newPost, setNewPost] = useState("");
    const [name, setName] = useState("");

    useEffect(async () => {
        const makeAPICall = async () => {
            const res = await fetch(`http://localhost:3001/post`);
            const data = await res.json();
            setPosts(data.posts);
        };
        makeAPICall();
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const res = await fetch(`http://localhost:3001/post`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                name,
                content: newPost,
            }),
        });
        const data = await res.json();
        setPosts(data.posts);

        // const post = {
        //     title: newPost,
        //     name: name,
        //     time: new Date().toLocaleString(), // Get the current time
        //     likes: 0, // Initialize likes to 0
        // };

        // setPosts([post, ...posts]);
        setNewPost(""); // clears the text area after submitting
        setName("");
    };

    const createPost = (event) => {
        setNewPost(event.target.value); // Update the newPost state
    };

    const handleNameChange = (event) => {
        setName(event.target.value); // Update the name state
    };

    const handleLike = (index) => {
        const updatedPosts = [...posts];
        updatedPosts[index].likes += 1;
        setPosts(updatedPosts);
    };

    return (
        <div className="mainPage">
            <div className="row">
                <div className="col-3"></div>
                <div
                    className="col-6"
                    style={{
                        background: "white",
                        padding: 10,
                        border: "1px solid gray",
                        marginBottom: 20,
                        borderRadius: 10,
                    }}
                >
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="nameInput" className="form-label">
                            Name:
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="nameInput"
                            placeholder="Your name"
                            value={name}
                            onChange={handleNameChange}
                        />
                        <label
                            htmlFor="titleInput"
                            className="form-label"
                        ></label>
                        <textarea
                            className="form-control"
                            id="titleInput"
                            placeholder="Post a new update for parents."
                            style={{ width: "100%" }}
                            onChange={(e) => setNewPost(e.target.value)}
                        ></textarea>
                        <div className="text-center">
                            <button className="btn btn-secondary">ðŸ“¸</button>
                            <button className="btn btn-secondary">ðŸŽ¥</button>
                            <button
                                type="submit"
                                className="btn btn-primary post-btn"
                            >
                                Post
                            </button>
                        </div>
                    </form>
                </div>
            </div>
            <div className="posts-list">
                {posts.map((post, index) => (
                    <div key={index} className="post-box">
                        <p>
                            <strong>{post.name}</strong> - {post.createdAt}
                        </p>
                        <p>{post.content}</p>
                        <div className="post-actions">
                            <button onClick={() => handleLike(index)}>
                                Like ({post.likes})
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
